/* General Synthwave Style */
html {
  font-family: 'Synthwave', sans-serif; /* Use a synthwave-inspired font */
  font-weight: bold;
  padding: 5vh;
  margin: 1vw;
  background: linear-gradient(to bottom, #000022, #111133); /* Dark gradient background */
  color: #FF69B4; /* Hot pink color */
  overflow-x: hidden; /* Prevent scrollbars */
}

body {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh; /* Full viewport height */
}

/* Dynamic Animations */
body {
  animation: neonPulse 2s infinite;
}

@keyframes neonPulse {
  0% {
    box-shadow: 0 0 20px #FF69B4, 0 0 40px #FF69B4;
  }
  50% {
    box-shadow: 0 0 30px #FF69B4, 0 0 50px #FF69B4;
  }
  100% {
    box-shadow: 0 0 20px #FF69B4, 0 0 40px #FF69B4;
  }
}

