input {
  background: rgba(0, 0, 0, 0.6);
  color: #FF69B4;
  border: 2px solid #FF69B4;
  height: 2rem;
  padding: 0.5rem;
  width: 200px;
  border-radius: 5px;
  font-size: 1rem;
  transition: background 0.3s, border 0.3s;
}

input:focus {
  outline: none;
  background: rgba(0, 0, 0, 0.8);
  border: 2px solid #FFC107; /* Yellow outline on focus */
}

/* Buttons */
button {
  background: linear-gradient(to right, #FF69B4, #FFC107); /* Gradient background */
  color: black;
  margin: 1rem;
  padding: 0.8rem 1.5rem;
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  cursor: pointer;
  transition: background 0.3s;
}

button:hover {
